for (var i=0; i<msg.payload.length; i++) {
    msg.payload[i].stat.created = msg.payload[i].stat.created.toISOString().slice(0, 19).replace('T', ' ');
    msg.payload[i].stat.changed = msg.payload[i].stat.changed.toISOString().slice(0, 19).replace('T', ' ');
    msg.payload[i].stat.accessed = msg.payload[i].stat.accessed.toISOString().slice(0, 19).replace('T', ' ');
    msg.payload[i].stat.statusChanged = msg.payload[i].stat.statusChanged.toISOString().slice(0, 19).replace('T', ' ');
    msg.payload[i].fname = msg.payload[i].name.replace(/^.*(\\|\/|\:)/, '');
}
return msg;

// Get the filename from the flow context
let filename = flow.get("fileselected");

// check, if the filename is undefined that means it does not exist yet, nothing is selected yet
// return: do not output anything
if (filename===undefined) {
    return;
}

// return the filename to the file-in node to delete
msg.filename = filename;

if (msg.filename.replace(/^.*(\\|\/|\:)/, '')[0]!==".") {
    // Only do this if this is a file, we don't delete folders
    // and delete the context/selection as we are deleting the file as well
    flow.set("fileselected");
    return msg;
}

msg.filename = msg.req.query.filename;
msg.contentdisposition = "attachment; filename=\"" + msg.req.query.filename.replace(/^.*(\\|\/|\:)/, '') + "\"";
return msg;

let folder = context.get("folder");
if (folder===undefined) {
    folder="/";
    context.set("folder", folder);
}

let hidden = context.get("hidden");
if (hidden===undefined) {
    hidden=false;
    context.set("hidden", hidden);
}

if (msg.topic==="up") {
    var the_arr = folder.split('/');
    the_arr.pop();
    folder=the_arr.join('/'); 
    context.set("folder", folder);
}
if (msg.topic==="change") {
    folder=msg.payload;    
    context.set("folder", folder);
}
if (msg.topic==="hidden") {
    hidden=msg.payload;    
    context.set("hidden", hidden);
}

msg.payload = {"start":folder, "hidden": hidden};
return msg;

// Get the filename from the flow context
